import React, { useState , useEffect} from 'react';
import { useDispatch ,useSelector} from 'react-redux';
import { createGround } from '../redux/actions/groundActions';
import {
  Box,
  TextField,
  Button,
  Typography,
  IconButton,
  Card,
  CardContent,
  Grid,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const CreateGround = () => {
  const [groundEntries, setGroundEntries] = useState([
    { 
      groundName: '', 
      location: '', 
      pricePerHour: '', 
      availability: '', 
      description: '', 
      slots: [{ slotName: '', startTime: '', endTime: '' }] // Initial slot array
    },
  ]);
  const dispatch = useDispatch();
  const { grounds } = useSelector(state => state.ground); // Access created grounds from Redux state

  useEffect(() => {
    // Fetch existing grounds on component mount
    dispatch(getGrounds());
  }, [dispatch]);


  // Handle ground input changes
  const onChange = (index, e) => {
    const newEntries = [...groundEntries];
    newEntries[index][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  // Handle slot input changes
  const handleSlotChange = (groundIndex, slotIndex, e) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots[slotIndex][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  // Add a new ground entry
  const addGroundEntry = () => {
    setGroundEntries([
      ...groundEntries, 
      { 
        groundName: '', 
        location: '', 
        pricePerHour: '', 
        availability: '', 
        description: '', 
        slots: [{ slotName: '', startTime: '', endTime: '' }] // New ground with initial slot
      }
    ]);
  };

  // Remove a ground entry
  const removeGroundEntry = (index) => {
    if (groundEntries.length > 1) { // Ensure at least one ground remains
      const newEntries = [...groundEntries];
      newEntries.splice(index, 1);
      setGroundEntries(newEntries);
    }
  };

  // Add a new slot to a specific ground
  const addSlot = (groundIndex) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots.push({ slotName: '', startTime: '', endTime: '' });
    setGroundEntries(newEntries);
  };

  // Remove a specific slot from a ground
  const removeSlot = (groundIndex, slotIndex) => {
    if (groundEntries[groundIndex].slots.length > 1) { // Ensure at least one slot remains
      const newEntries = [...groundEntries];
      newEntries[groundIndex].slots.splice(slotIndex, 1);
      setGroundEntries(newEntries);
    }
  };

  // Submit form to create grounds with slots
  const onSubmit = async (e) => {
    e.preventDefault();
    for (const entry of groundEntries) {
      await dispatch(createGround(entry)); // Dispatch create ground action for each ground with its slots
    }
    //Later
    //alert("Grounds with slots created successfully!"); // Notify user
  };

  return (
    <Box
      sx={{
        width: '600px',
        margin: 'auto',
        marginTop: '50px',
        padding: '20px',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <Typography variant="h4" align="center" gutterBottom style={{ color: '#3f51b5' }}>
        Create Grounds and Slots
      </Typography>
      <form onSubmit={onSubmit}>
        {groundEntries.map((entry, groundIndex) => (
          <Box key={groundIndex} sx={{ marginBottom: '20px' }}>
            <Typography variant="h6" gutterBottom>Ground {groundIndex + 1}</Typography>

            {/* Ground Details */}
            <TextField
              fullWidth
              margin="normal"
              label="Ground Name"
              variant="outlined"
              name="groundName"
              value={entry.groundName}
              onChange={(e) => onChange(groundIndex, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Location"
              variant="outlined"
              name="location"
              value={entry.location}
              onChange={(e) => onChange(groundIndex, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Price per Hour"
              variant="outlined"
              name="pricePerHour"
              value={entry.pricePerHour}
              onChange={(e) => onChange(groundIndex, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Availability"
              variant="outlined"
              name="availability"
              value={entry.availability}
              onChange={(e) => onChange(groundIndex, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Description"
              variant="outlined"
              name="description"
              value={entry.description}
              onChange={(e) => onChange(groundIndex, e)}
              multiline
              rows={4}
              required
            />

            {/* Slot Details for the Ground */}
            <Typography variant="subtitle1" gutterBottom>Slots</Typography>
            {entry.slots.map((slot, slotIndex) => (
              <Box key={slotIndex} sx={{ marginBottom: '10px' }}>
                <TextField
                  fullWidth
                  margin="normal"
                  label="Slot Name"
                  variant="outlined"
                  name="slotName"
                  value={slot.slotName}
                  onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                  required
                />
                <TextField
                  fullWidth
                  margin="normal"
                  label="Start Time"
                  variant="outlined"
                  name="startTime"
                  type="time"
                  value={slot.startTime}
                  onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                  required
                />
                <TextField
                  fullWidth
                  margin="normal"
                  label="End Time"
                  variant="outlined"
                  name="endTime"
                  type="time"
                  value={slot.endTime}
                  onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                  required
                />
                {entry.slots.length > 1 && (
                  <IconButton onClick={() => removeSlot(groundIndex, slotIndex)} color="error" aria-label="remove slot">
                    <DeleteIcon />
                  </IconButton>
                )}
              </Box>
            ))}
            <Button
              onClick={() => addSlot(groundIndex)}
              variant="outlined"
              color="primary"
              startIcon={<AddIcon />}
              sx={{ marginTop: '10px', marginBottom: '10px' }}
            >
              Add Another Slot
            </Button>

            {/* Remove Ground Button */}
            {groundEntries.length > 1 && (
              <IconButton onClick={() => removeGroundEntry(groundIndex)} color="error" aria-label="remove ground">
                <DeleteIcon />
              </IconButton>
            )}
          </Box>
        ))}
        <Button
          onClick={addGroundEntry}
          variant="outlined"
          color="primary"
          startIcon={<AddIcon />}
          sx={{ marginTop: '20px', marginBottom: '20px' }}
        >
          Add Another Ground
        </Button>
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
        >
          Create Grounds and Slots
        </Button>
      </form>

      {/* Display Created Grounds with Slots */}
      <Box sx={{ marginTop: '50px' }}>
        <Typography variant="h5" gutterBottom>
          Created Grounds
        </Typography>
        <Grid container spacing={2}>
          {grounds && grounds.map((ground) => (
            <Grid item xs={12} sm={6} key={ground._id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{ground.groundName}</Typography>
                  <Typography>Location: {ground.location}</Typography>
                  <Typography>Price per Hour: {ground.pricePerHour}</Typography>
                  <Typography>Availability: {ground.availability}</Typography>
                  <Typography>Description: {ground.description}</Typography>

                  {/* Display the slots */}
                  <Typography variant="subtitle1" gutterBottom>Slots:</Typography>
                  {ground.slots.map((slot, index) => (
                    <Typography key={index}>
                      {slot.slotName} - {slot.startTime} to {slot.endTime}
                    </Typography>
                  ))}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
    // Show Entries //
    
  );
};

export default CreateGround;
