import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createGround, fetchGrounds } from '../redux/actions/groundActions';
import {
  Box,
  TextField,
  Button,
  Typography,
  IconButton,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const CreateGround = () => {
  const [groundEntries, setGroundEntries] = useState([
    { 
      groundName: '', 
      location: '', 
      pricePerHour: '', 
      availability: '', 
      description: '', 
      slots: [{ slotName: '', startTime: '', endTime: '' }] // Initial slot array
    },
  ]);
  const [isViewing, setIsViewing] = useState(false); // State to toggle between view and create mode
  const dispatch = useDispatch();
  
  // Fetching existing grounds
  const grounds = useSelector((state) => state.grounds.grounds); // Make sure to adjust based on your Redux state structure
  console.log(grounds, "gounfs");

  useEffect(() => {
    dispatch(fetchGrounds());
  }, [dispatch]);

  // Handle ground input changes
  const onChange = (index, e) => {
    const newEntries = [...groundEntries];
    newEntries[index][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  // Handle slot input changes
  const handleSlotChange = (groundIndex, slotIndex, e) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots[slotIndex][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  // Add a new ground entry
  const addGroundEntry = () => {
    setGroundEntries([
      ...groundEntries, 
      { 
        groundName: '', 
        location: '', 
        pricePerHour: '', 
        availability: '', 
        description: '', 
        slots: [{ slotName: '', startTime: '', endTime: '' }] // New ground with initial slot
      }
    ]);
  };

  // Remove a ground entry
  const removeGroundEntry = (index) => {
    if (groundEntries.length > 1) { // Ensure at least one ground remains
      const newEntries = [...groundEntries];
      newEntries.splice(index, 1);
      setGroundEntries(newEntries);
    }
  };

  // Add a new slot to a specific ground
  const addSlot = (groundIndex) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots.push({ slotName: '', startTime: '', endTime: '' });
    setGroundEntries(newEntries);
  };

  // Remove a specific slot from a ground
  const removeSlot = (groundIndex, slotIndex) => {
    if (groundEntries[groundIndex].slots.length > 1) { // Ensure at least one slot remains
      const newEntries = [...groundEntries];
      newEntries[groundIndex].slots.splice(slotIndex, 1);
      setGroundEntries(newEntries);
    }
  };

  // Submit form to create grounds with slots
  const onSubmit = async (e) => {
    e.preventDefault();
    for (const entry of groundEntries) {
      await dispatch(createGround(entry)); // Dispatch create ground action for each ground with its slots
    }
    alert("Grounds with slots created successfully!"); // Notify user
  };

  // Function to toggle between create and view mode
  const toggleViewMode = () => {
    setIsViewing((prev) => !prev);
  };

  return (
    <Box
      sx={{
        width: '600px',
        margin: 'auto',
        marginTop: '50px',
        padding: '20px',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <Typography variant="h4" align="center" gutterBottom style={{ color: '#3f51b5' }}>
        {isViewing ? "View Grounds" : "Create Grounds and Slots"}
      </Typography>
      {isViewing ? (
        <Box>
          {grounds.length > 0 ? (
            grounds.map((entry, groundIndex) => (
              <Box key={groundIndex} sx={{ marginBottom: '20px' }}>
                <Typography variant="h6" gutterBottom>{`Ground ${groundIndex + 1}`}</Typography>
                <Typography><strong>Ground Name:</strong> {entry.groundName}</Typography>
                <Typography><strong>Location:</strong> {entry.location}</Typography>
                <Typography><strong>Price per Hour:</strong> {entry.pricePerHour}</Typography>
                <Typography><strong>Availability:</strong> {entry.availability}</Typography>
                <Typography><strong>Description:</strong> {entry.description}</Typography>
                <Typography variant="subtitle1" gutterBottom>Slots:</Typography>
                {entry.slots.map((slot, slotIndex) => (
                  <Typography key={slotIndex}>
                    {`${slot.slotName} (${slot.startTime} - ${slot.endTime})`}
                  </Typography>
                ))}
              </Box>
            ))
          ) : (
            <Typography>No grounds available.</Typography>
          )}
          <Button variant="outlined" onClick={toggleViewMode} color="primary">
            Create New Ground
          </Button>
        </Box>
      ) : (
        <form onSubmit={onSubmit}>
          {groundEntries.map((entry, groundIndex) => (
            <Box key={groundIndex} sx={{ marginBottom: '20px' }}>
              <Typography variant="h6" gutterBottom>Ground {groundIndex + 1}</Typography>

              {/* Ground Details */}
              <TextField
                fullWidth
                margin="normal"
                label="Ground Name"
                variant="outlined"
                name="groundName"
                value={entry.groundName}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Location"
                variant="outlined"
                name="location"
                value={entry.location}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Price per Hour"
                variant="outlined"
                name="pricePerHour"
                value={entry.pricePerHour}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Availability"
                variant="outlined"
                name="availability"
                value={entry.availability}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Description"
                variant="outlined"
                name="description"
                value={entry.description}
                onChange={(e) => onChange(groundIndex, e)}
                multiline
                rows={4}
                required
              />

              {/* Slot Details for the Ground */}
              <Typography variant="subtitle1" gutterBottom>Slots</Typography>
              {entry.slots.map((slot, slotIndex) => (
                <Box key={slotIndex} sx={{ marginBottom: '10px' }}>
                  <TextField
                    fullWidth
                    margin="normal"
                    label="Slot Name"
                    variant="outlined"
                    name="slotName"
                    value={slot.slotName}
                    onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                    required
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    label="Start Time"
                    variant="outlined"
                    name="startTime"
                    type="time"
                    value={slot.startTime}
                    onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                    required
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    label="End Time"
                    variant="outlined"
                    name="endTime"
                    type="time"
                    value={slot.endTime}
                    onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                    required
                  />
                  {entry.slots.length > 1 && (
                    <IconButton onClick={() => removeSlot(groundIndex, slotIndex)} color="error" aria-label="remove slot">
                      <DeleteIcon />
                    </IconButton>
                  )}
                </Box>
              ))}
              <Button
                onClick={() => addSlot(groundIndex)}
                variant="outlined"
                color="primary"
                startIcon={<AddIcon />}
                sx={{ marginTop: '10px', marginBottom: '10px' }}
              >
                Add Another Slot
              </Button>

              {/* Remove Ground Button */}
              {groundEntries.length > 1 && (
                <IconButton onClick={() => removeGroundEntry(groundIndex)} color="error" aria-label="remove ground">
                  <DeleteIcon />
                </IconButton>
              )}
            </Box>
          ))}

          {/* Add Another Ground Button */}
          <Button
            onClick={addGroundEntry}
            variant="outlined"
            color="primary"
            startIcon={<AddIcon />}
            sx={{ marginBottom: '20px' }}
          >
            Add Another Ground
          </Button>

          <Button type="submit" variant="contained" color="primary" fullWidth>
            Create Grounds
          </Button>
          <Button variant="outlined" onClick={toggleViewMode} color="secondary" fullWidth>
            View Existing Grounds
          </Button>
        </form>
      )}
    </Box>
  );
};

export default CreateGround;
