import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { createGround } from '../redux/actions/groundActions';
import {
  Box,
  TextField,
  Button,
  Typography,
  Grid,
  IconButton,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const CreateGround = () => {
  const [groundEntries, setGroundEntries] = useState([
    { groundName: '', location: '', pricePerHour: '', availability: '', description: '' },
  ]);
  const dispatch = useDispatch();

  const onChange = (index, e) => {
    const newEntries = [...groundEntries];
    newEntries[index][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  const addGroundEntry = () => {
    setGroundEntries([...groundEntries, { groundName: '', location: '', pricePerHour: '', availability: '', description: '' }]);
  };

  const removeGroundEntry = (index) => {
    const newEntries = [...groundEntries];
    newEntries.splice(index, 1);
    setGroundEntries(newEntries);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    for (const entry of groundEntries) {
      await dispatch(createGround(entry)); // Dispatch the create ground action for each entry
    }
    alert("Grounds created successfully!"); // Notify the user
  };

  return (
    <Box
      sx={{
        width: '600px',
        margin: 'auto',
        marginTop: '50px',
        padding: '20px',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <Typography variant="h4" align="center" gutterBottom style={{ color: '#3f51b5' }}>
        Create Grounds
      </Typography>
      <form onSubmit={onSubmit}>
        {groundEntries.map((entry, index) => (
          <Box key={index} sx={{ marginBottom: '20px' }}>
            <Typography variant="h6" gutterBottom>Ground {index + 1}</Typography>
            <TextField
              fullWidth
              margin="normal"
              label="Ground Name"
              variant="outlined"
              name="groundName"
              value={entry.groundName}
              onChange={(e) => onChange(index, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Location"
              variant="outlined"
              name="location"
              value={entry.location}
              onChange={(e) => onChange(index, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Price per Hour"
              variant="outlined"
              name="pricePerHour"
              value={entry.pricePerHour}
              onChange={(e) => onChange(index, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Availability"
              variant="outlined"
              name="availability"
              value={entry.availability}
              onChange={(e) => onChange(index, e)}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Description"
              variant="outlined"
              name="description"
              value={entry.description}
              onChange={(e) => onChange(index, e)}
              multiline
              rows={4}
              required
            />
            {index > 0 && (
              <IconButton onClick={() => removeGroundEntry(index)} color="error" aria-label="remove ground">
                <DeleteIcon />
              </IconButton>
            )}
          </Box>
        ))}
        <Button
          onClick={addGroundEntry}
          variant="outlined"
          color="primary"
          startIcon={<AddIcon />}
          sx={{ marginTop: '20px', marginBottom: '20px' }}
        >
          Add Another Ground
        </Button>
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
        >
          Create Grounds
        </Button>
      </form>
    </Box>
  );
};

export default CreateGround;
