import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createGround } from '../redux/actions/groundActions';
import { Box, Typography, Card, CardContent } from '@mui/material';

const AdminDashboard = () => {
  const dispatch = useDispatch();
  const { grounds, loading, error } = useSelector((state) => state.grounds);

  useEffect(() => {
    dispatch(createGround()); // Fetch grounds when the component mounts
  }, [dispatch]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <Box sx={{ margin: '20px' }}>
      <Typography variant="h4" gutterBottom>My Grounds</Typography>
      {grounds.length === 0 ? (
        <Typography>No grounds found.</Typography>
      ) : (
        grounds.map((ground) => (
          <Card key={ground.id} sx={{ marginBottom: '20px' }}>
            <CardContent>
              <Typography variant="h5">{ground.groundName}</Typography>
              <Typography>Location: {ground.location}</Typography>
              <Typography>Price per Hour: ${ground.pricePerHour}</Typography>
              <Typography>Availability: {ground.availability}</Typography>
              <Typography>Description: {ground.description}</Typography>
            </CardContent>
          </Card>
        ))
      )}
    </Box>
  );
};

export default AdminDashboard;
