// frontend/src/components/Register.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Box, TextField, Button, Typography, Select, MenuItem, InputLabel, FormControl } from '@mui/material';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'user', // default value is 'user'
  });

  const navigate = useNavigate(); // hook for navigation

  const { username, email, password, role } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/register', formData);
      console.log('User registered successfully:', res.data);
      alert(res.data.msg);
      if (res.status === 201) {
        navigate('/login'); // Navigate to login screen
      }
    } catch (err) {
      console.error('Registration error:', err.response.data);
      alert(err.response.data.msg);
    }
  };

  return (
    <Box
      sx={{
        width: '400px',
        margin: 'auto',
        marginTop: '50px',
        padding: '20px',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <Typography variant="h4" align="center" gutterBottom style={{ color: '#3f51b5' }}>
        Register
      </Typography>
      <form onSubmit={onSubmit}>
        <TextField
          fullWidth
          margin="normal"
          label="Username"
          variant="outlined"
          name="username"
          value={username}
          onChange={onChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Email"
          type="email"
          variant="outlined"
          name="email"
          value={email}
          onChange={onChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Password"
          type="password"
          variant="outlined"
          name="password"
          value={password}
          onChange={onChange}
          required
        />
        <FormControl fullWidth margin="normal">
\          <Select name="role" value={role} onChange={onChange}>
            <MenuItem value="user">User</MenuItem>
            <MenuItem value="groundOwner">GroundOwner</MenuItem>
          </Select>
        </FormControl>
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          sx={{ marginTop: '20px', padding: '10px 0' }}
        >
          Register
        </Button>
      </form>
    </Box>
  );
};

export default Register;
