        //         name="location"
        //         value={entry.location}
        //         onChange={(e) => onChange(groundIndex, e)}
        //         required
        //       />
        //       <TextField
        //         fullWidth
        //         margin="normal"
        //         label="Price per Hour"
        //         variant="outlined"
        //         name="pricePerHour"
        //         value={entry.pricePerHour}
        //         onChange={(e) => onChange(groundIndex, e)}
        //         required
        //       />
        //       <TextField
        //         fullWidth
        //         margin="normal"
        //         label="Availability"
        //         variant="outlined"
        //         name="availability"
        //         value={entry.availability}
        //         onChange={(e) => onChange(groundIndex, e)}
        //         required
        //       />
        //       <TextField
        //         fullWidth
        //         margin="normal"
        //         label="Description"
        //         variant="outlined"
        //         name="description"
        //         value={entry.description}
        //         onChange={(e) => onChange(groundIndex, e)}
        //         multiline
        //         rows={4}
        //         required
        //       />

        //       {/* Slot Details for the Ground */}
        //       <Typography variant="subtitle1" gutterBottom>Slots</Typography>
        //       {entry.slots.map((slot, slotIndex) => (
        //         <Box key={slotIndex} sx={{ marginBottom: '10px' }}>
        //           <TextField
        //             fullWidth
        //             margin="normal"
        //             label="Slot Name"
        //             variant="outlined"
        //             name="slotName"
        //             value={slot.slotName}
        //             onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
        //             required
        //           />
        //           <TextField
        //             fullWidth
        //             margin="normal"
        //             label="Start Time"
        //             variant="outlined"
        //             name="startTime"
        //             type="time"
        //             value={slot.startTime}
        //             onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
        //             required
        //           />
        //           <TextField
        //             fullWidth
