import axios from 'axios';

export const createGround = (groundData) => async (dispatch) => {
  dispatch({ type: 'CREATE_GROUND_REQUEST' });
  try {
    const res = await axios.post('http://localhost:5000/api/grounds', groundData); // Adjust API endpoint as needed
    dispatch({ type: 'CREATE_GROUND_SUCCESS', payload: res.data });
  } catch (err) {
    dispatch({ type: 'CREATE_GROUND_FAILURE', payload: err.response.data.msg });
  }
};
