import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { createGround } from '../redux/actions/groundActions'; // Import the action for creating a ground
import {
  Box,
  TextField,
  Button,
  Typography,
} from '@mui/material';

const CreateGround = () => {
  const [formData, setFormData] = useState({
    groundName: '',
    location: '',
    pricePerHour: '',
    availability: '',
    description: '',
  });

  const dispatch = useDispatch();

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await dispatch(createGround(formData)); // Dispatch the create ground action
    alert("Ground created successfully!"); // Notify the user
  };

  return (
    <Box
      sx={{
        width: '400px',
        margin: 'auto',
        marginTop: '50px',
        padding: '20px',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <Typography variant="h4" align="center" gutterBottom style={{ color: '#3f51b5' }}>
        Create Ground
      </Typography>
      <form onSubmit={onSubmit}>
        <TextField
          fullWidth
          margin="normal"
          label="Ground Name"
          variant="outlined"
          name="groundName"
          value={formData.groundName}
          onChange={onChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Location"
          variant="outlined"
          name="location"
          value={formData.location}
          onChange={onChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Price per Hour"
          variant="outlined"
          name="pricePerHour"
          value={formData.pricePerHour}
          onChange={onChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Availability"
          variant="outlined"
          name="availability"
          value={formData.availability}
          onChange={onChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Description"
          variant="outlined"
          name="description"
          value={formData.description}
          onChange={onChange}
          multiline
          rows={4}
          required
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          sx={{ marginTop: '20px', padding: '10px 0' }}
        >
          Create Ground
        </Button>
      </form>
    </Box>
  );
};

export default CreateGround;
