import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createGround, fetchGrounds } from '../redux/actions/groundActions';
import {
  Box,
  TextField,
  Button,
  Typography,
  IconButton,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const CreateGround = () => {
  const [groundEntries, setGroundEntries] = useState([
    { 
      groundName: '', 
      location: '', 
      pricePerHour: '', 
      availability: '', 
      description: '', 
      slots: [{ slotName: '', startTime: '', endTime: '' }] // Initial slot array
    },
  ]);

  const [isCreatingNewGround, setIsCreatingNewGround] = useState(false); // New state to control ground creation

  const dispatch = useDispatch();
  const groundState = useSelector((state) => state.grounds);
  const { grounds, loading } = groundState;
  let check  = groundState.grounds.grounds;

  useEffect(() => {
    dispatch(fetchGrounds()); // Fetch existing grounds when component mounts
  }, [dispatch]);

  if (loading) {
    return <div>Loading...</div>;
  }

  // Add a new ground entry
  const addGroundEntry = () => {
    setGroundEntries((prev) => [
      ...prev, 
      { 
        groundName: '', 
        location: '', 
        pricePerHour: '', 
        availability: '', 
        description: '', 
        slots: [{ slotName: '', startTime: '', endTime: '' }] // New ground with initial slot
      }
    ]);
  };

  // Delete a ground entry
  const deleteGroundEntry = (index) => {
    setGroundEntries((prev) => prev.filter((_, i) => i !== index));
  };

  // Add a new slot to a ground
  const addSlot = (groundIndex) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots.push({ slotName: '', startTime: '', endTime: '' });
    setGroundEntries(newEntries);
  };

  // Delete a slot from a ground
  const deleteSlot = (groundIndex, slotIndex) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots = newEntries[groundIndex].slots.filter((_, i) => i !== slotIndex);
    setGroundEntries(newEntries);
  };

  // Handle ground input changes
  const onChange = (index, e) => {
    const newEntries = [...groundEntries];
    newEntries[index][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  // Handle slot input changes
  const handleSlotChange = (groundIndex, slotIndex, e) => {
    const newEntries = [...groundEntries];
    newEntries[groundIndex].slots[slotIndex][e.target.name] = e.target.value;
    setGroundEntries(newEntries);
  };

  // Submit form to create grounds with slots
  const onSubmit = async (e) => {
    e.preventDefault();
    for (const entry of groundEntries) {
      await dispatch(createGround(entry));
    }
    alert("Grounds with slots created successfully!");
    dispatch(fetchGrounds());
  };

  return (
    <Box
      sx={{
        width: '600px',
        margin: 'auto',
        marginTop: '50px',
        padding: '20px',
        backgroundColor: '#f5f5f5',
        borderRadius: '8px',
        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <Typography variant="h4" align="center" gutterBottom style={{ color: '#3f51b5' }}>
        {check.length > 0 ? "View Existing Grounds" : "Create a New Ground"}
      </Typography>

      {check.length > 0 && !isCreatingNewGround ? (
        <Box>
          {check.map((entry, groundIndex) => (
            <Box key={groundIndex} sx={{ marginBottom: '20px' }}>
              <Typography variant="h6" gutterBottom>{`Ground ${groundIndex + 1}`}</Typography>
              <Typography><strong>Ground Name:</strong> {entry.groundName}</Typography>
              <Typography><strong>Location:</strong> {entry.location}</Typography>
              <Typography><strong>Price per Hour:</strong> {entry.pricePerHour}</Typography>
              <Typography><strong>Availability:</strong> {entry.availability}</Typography>
              <Typography><strong>Description:</strong> {entry.description}</Typography>
              <Typography variant="subtitle1" gutterBottom>Slots:</Typography>
              {entry.slots.map((slot, slotIndex) => (
                <Typography key={slotIndex}>
                  {`${slot.slotName} (${slot.startTime} - ${slot.endTime})`}
                </Typography>
              ))}
            </Box>
          ))}
          {/* Button to create a new ground */}
          <Button
            variant="outlined"
            color="primary"
            startIcon={<AddIcon />}
            sx={{ marginTop: '20px' }}
            onClick={() => setIsCreatingNewGround(true)} // Set to true to show the form
          >
            Create Ground
          </Button>
        </Box>
      ) : (
        <form onSubmit={onSubmit}>
          {groundEntries.map((entry, groundIndex) => (
            <Box key={groundIndex} sx={{ marginBottom: '20px' }}>
              <Typography variant="h6" gutterBottom>Ground {groundIndex + 1}</Typography>
              
              {/* Delete ground button */}
              <IconButton
                aria-label="delete"
                onClick={() => deleteGroundEntry(groundIndex)}
              >
                <DeleteIcon />
              </IconButton>

              <TextField
                fullWidth
                margin="normal"
                label="Ground Name"
                variant="outlined"
                name="groundName"
                value={entry.groundName}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Location"
                variant="outlined"
                name="location"
                value={entry.location}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Price per Hour"
                variant="outlined"
                name="pricePerHour"
                value={entry.pricePerHour}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Availability"
                variant="outlined"
                name="availability"
                value={entry.availability}
                onChange={(e) => onChange(groundIndex, e)}
                required
              />
              <TextField
                fullWidth
                margin="normal"
                label="Description"
                variant="outlined"
                name="description"
                value={entry.description}
                onChange={(e) => onChange(groundIndex, e)}
                multiline
                rows={4}
                required
              />
              <Typography variant="subtitle1" gutterBottom>Slots</Typography>
              {entry.slots.map((slot, slotIndex) => (
                <Box key={slotIndex} sx={{ marginBottom: '10px' }}>
                  <TextField
                    fullWidth
                    margin="normal"
                    label="Slot Name"
                    variant="outlined"
                    name="slotName"
                    value={slot.slotName}
                    onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                    required
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    label="Start Time"
                    variant="outlined"
                    name="startTime"
                    type="time"
                    value={slot.startTime}
                    onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                    required
                  />
                  <TextField
                    fullWidth
                    margin="normal"
                    label="End Time"
                    variant="outlined"
                    name="endTime"
                    type="time"
                    value={slot.endTime}
                    onChange={(e) => handleSlotChange(groundIndex, slotIndex, e)}
                    required
                  />

                  {/* Delete slot button */}
                  <IconButton
                    aria-label="delete"
                    onClick={() => deleteSlot(groundIndex, slotIndex)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              ))}
              {/* Add slot button */}
              <Button
                onClick={() => addSlot(groundIndex)}
                variant="outlined"
                color="primary"
                startIcon={<AddIcon />}
                sx={{ marginTop: '10px', marginBottom: '20px' }}
              >
                Add Slot
              </Button>
            </Box>
          ))}
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Create Grounds
          </Button>
        </form>
      )}
    </Box>
  );
};

export default CreateGround
